function [y,dy,d2y,c_0,c_1,c_2,c_3]=vrij_kub_splajna(t, x, f, s)%t- tocka u kojoj se racuna vrijednost splajna%x-vektor intepolacijskih ?vorova x%f-vektor intepolacijskih vrijednosti u cvorovima f%s-vektor parametara, vrijednosti derivacije splajna u cvorivima %y vrijednost splajna u tocki t%dy vrijednost derivacije splajna u tocki t %d2y vrijednost druge derivacije splajna u tocki t n=length(x)-1;k=bintrazenje(t,n,x)+1; %nalazimo u kojem intervalu pripada tc_0=f(k-1);c_1=s(k-1);h=x(k)-x(k-1);pom=(f(k)-f(k-1))/h;c_3=(s(k)+s(k-1)-2*pom)/h^2; %sa slajdac_2=(pom-s(k-1))/h-h*c_3;     %sa slajdap=t-x(k-1);koef(1)=c_0;koef(2)=c_1;koef(3)=c_2;koef(4)=c_3;zbroj=koef(4);for i=3:-1:1   zbroj=zbroj*p+koef(i);endy=zbroj;%racunamo za prvu derivacijukoef(1)=c_1;koef(2)=2*c_2;koef(3)=3*c_3;zbroj=koef(3);for i=2:-1:1  zbroj=zbroj*p+koef(i);end%racunamo za drugu derivacijudy=zbroj;d2y=2*c_2+6*c_3*p;end